mapper.select:
select * from ( ( SELECT t1.id as creUserId, t1.user_phone, t2.username as custName, t4.company_name, t1.create_time, t5.account, t1.user_type, t2.id, t4.id as agentId FROM o_agent_cre_user t3 LEFT JOIN o_agent t4 ON t3.agent_id = t4.id LEFT JOIN cre_user t1 ON t3.cre_user_id = t1.id LEFT JOIN idcardinfo t2 ON t2.cre_user_id = t1.id LEFT JOIN cre_user_account t5 ON t5.cre_user_id = t1.id
LEFT JOIN o_agent_sys_user t6 on t6.agent_id=t4.id LEFT JOIN o_sys_user t7 on t7.user_id=t6.sys_user_id
WHERE t4.id is not null
and t7.user_id=#{sysUserId}
and ( t1.user_type = 0 or t1.user_type is null )
and t1.create_time >= #{startTimeStr}
and t1.create_time < #{endTimeStr}
and t4.company_name like concat('%',#{agentName},'%')
and t2.username like concat('%',#{custName},'%')
and t1.user_phone like concat('%',#{mobile},'%')
) union all ( SELECT t1.id as creUserId, t1.user_phone, t2.name as custName, t4.company_name, t1.create_time, t5.account, t1.user_type, t2.id, t4.id as agentId FROM o_agent_cre_user t3 LEFT JOIN o_agent t4 ON t3.agent_id = t4.id LEFT JOIN cre_user t1 ON t3.cre_user_id = t1.id LEFT JOIN businesslicenceinfo t2 ON t2.cre_user_id = t1.id LEFT JOIN cre_user_account t5 ON t5.cre_user_id = t1.id
LEFT JOIN o_agent_sys_user t6 on t6.agent_id=t4.id LEFT JOIN o_sys_user t7 on t7.user_id=t6.sys_user_id
WHERE t4.id is not null
and t7.user_id=#{sysUserId}
and t1.user_type = 1
and t1.create_time >= #{startTimeStr}
and t1.create_time < #{endTimeStr}
and t4.company_name like concat('%',#{agentName},'%')
and t2.name like concat('%',#{custName},'%')
and t1.user_phone like concat('%',#{mobile},'%')
) ) tt ORDER BY tt.create_time desc
